sysauto_script.script:
(function(){

	var getActiveSurveys = function(){

		var activeSurveys = [];
		var srvy = new GlideRecord('x_kasp_business_su_business_surveys');	srvy.addEncodedQuery('start_date<=javascript:gs.daysAgoEnd(0)^end_date>javascript:gs.daysAgoEnd(0)');
		srvy.query();
		if(srvy.next()){
			activeSurveys.push(srvy);
		}
		return activeSurveys;
	};

	var surveyList = getActiveSurveys();

	for(var i=0; i < surveyList.length;i++){
		gs.info("Business Survey sending responses for " + surveyList[i].getValue('name'));
		gs.eventQueue('x_kasp_business_su.weekly_report_event',surveyList[i],null,null,null);
	}


})();
